def dfs_recursive(graph, start, visited=None, order=None):
    if visited is None:
        visited = set()
    if order is None:
        order = []
    visited.add(start)
    order.append(start)
    for n in graph.get(start, []):
        if n not in visited:
            dfs_recursive(graph, n, visited, order)
    return order


def dfs_iterative(graph, start):
    visited = set()
    stack = []
    order = []
    if start not in graph:
        return order
    stack.append(start)
    while stack:
        v = stack.pop()
        if v not in visited:
            visited.add(v)
            order.append(v)
            for n in reversed(graph.get(v, [])):
                if n not in visited:
                    stack.append(n)
    return order


