def is_variable(x):
    return isinstance(x, str) and x[0].islower()


def substitute(term, subs):
    if isinstance(term, str):
        return subs.get(term, term)
    functor, arg = term
    return (functor, substitute(arg, subs))


def occurs_check(var, term, subs):
    term = substitute(term, subs)
    if var == term:
        return True
    if isinstance(term, tuple):
        return occurs_check(var, term[1], subs)
    return False


def unify(x, y, subs=None):
    if subs is None:
        subs = {}
    x = substitute(x, subs)
    y = substitute(y, subs)
    if x == y:
        return subs
    if is_variable(x):
        if occurs_check(x, y, subs):
            return None
        subs[x] = y
        return subs
    if is_variable(y):
        if occurs_check(y, x, subs):
            return None
        subs[y] = x
        return subs
    if isinstance(x, tuple) and isinstance(y, tuple):
        fx, ax = x
        fy, ay = y
        if fx != fy:
            return None
        return unify(ax, ay, subs)
    return None


