facts = set()
rules = []


def add_fact(f):
    facts.add(f)


def add_rule(conditions, conclusion):
    rules.append((set(conditions), conclusion))


def infer():
    changed = True
    while changed:
        changed = False
        for cond, conc in rules:
            if cond.issubset(facts) and conc not in facts:
                facts.add(conc)
                changed = True


